CREATE OR REPLACE FUNCTION obter_se_integra_item_req ( nr_requisicao_p bigint, cd_local_estoque_p bigint, cd_local_estoque_destino_p bigint, cd_operacao_estoque_p bigint, cd_material_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
A - Quando atende o item
L - Quando libera a requisição*/
ie_integra_w			varchar(1) := 'N';
qt_existe_w			integer;
cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;


BEGIN
select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material = cd_material_p;

select	count(*)
into STRICT	qt_existe_w
from	sup_int_regra_req
where	coalesce(cd_grupo_material, cd_grupo_material_w)			= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)		= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)			= cd_classe_material_w
and	coalesce(cd_material, cd_material_p) 				= cd_material_p
and	coalesce(cd_operacao_estoque, cd_operacao_estoque_p)		= cd_operacao_estoque_p
and	coalesce(cd_local_estoque, cd_local_estoque_p)			= cd_local_estoque_p
and (coalesce(cd_local_estoque_destino, cd_local_estoque_destino_p)	= cd_local_estoque_destino_p or coalesce(cd_local_estoque_destino_p::text, '') = '')
and	ie_atende = ie_opcao_p;

if (qt_existe_w > 0) then
	ie_integra_w := 'S';
end if;

return	ie_integra_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
