CREATE OR REPLACE FUNCTION obter_se_integra_lwsdeg_vfr ( nr_prescricao_p bigint, cd_estabelecimento_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_integra_w		varchar(1) := 'N';


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_integra_w
	from	prescr_procedimento b,
		exame_laboratorio c,
		lab_exame_equip d,
		equipamento_lab	e,
		material_exame_lab g
	where	b.nr_prescricao = nr_prescricao_p
	and	b.nr_seq_exame = c.nr_seq_exame
	and	c.nr_seq_exame = d.nr_seq_exame
	and	d.cd_equipamento = e.cd_equipamento
	and	g.cd_material_exame = b.cd_material_exame
	and	g.nr_sequencia = coalesce(d.nr_seq_material,g.nr_sequencia)
	and	(b.nr_seq_lab IS NOT NULL AND b.nr_seq_lab::text <> '')
	and	(b.nr_seq_exame IS NOT NULL AND b.nr_seq_exame::text <> '')
	and	coalesce(b.dt_integracao::text, '') = ''
	and	b.cd_motivo_baixa = 0
	and 	e.ds_sigla	= 'LWSDEG';

end if;

return	ie_integra_w;

end;
$body$
