CREATE OR REPLACE FUNCTION obter_se_integra_res ( nr_atendimento_p text) RETURNS varchar AS $body$
DECLARE


return_w				varchar(4000);
cd_convenio_w			bigint;


BEGIN

SELECT OBTER_CONVENIO_ATENDIMENTO(nr_atendimento_p)
into STRICT cd_convenio_w
;

	if (cd_convenio_w IS NOT NULL AND cd_convenio_w::text <> '') then

		Select  coalesce(max('S'),'N')
		into STRICT	return_w
		from    conversao_meio_externo
		where   upper(cd_externo) = 'RESUNIMED'
		and		upper(nm_tabela) = 'ATEND_CATEGORIA_CONVENIO'
		and     cd_interno = to_char(cd_convenio_w);

	end if;

return return_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
