CREATE OR REPLACE FUNCTION obter_se_integr_movto_estoque ( nr_movimento_estoque_p bigint, ie_integracao_p text default 'EMS', cd_operacao_estoque_p bigint default null, cd_local_estoque_p bigint default null, cd_material_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
ie_entrada_saida_w	varchar(1);
cd_operacao_estoque_w	smallint;
cd_material_w		integer;
cd_local_estoque_w	smallint;
cd_grupo_material_w	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;
nr_seq_regra_w		bigint;
ie_integra_w		varchar(1);


BEGIN
if (coalesce(nr_movimento_estoque_p,0) > 0) then
	begin
	select	cd_operacao_estoque,
		cd_material,
		cd_local_estoque
	into STRICT	cd_operacao_estoque_w,
		cd_material_w,
		cd_local_estoque_w
	from	movimento_estoque
	where 	nr_movimento_estoque = nr_movimento_estoque_p;
	end;
else
	begin
	cd_local_estoque_w	:=	cd_local_estoque_p;
	cd_operacao_estoque_w	:=	cd_operacao_estoque_p;
	cd_material_w		:=	cd_material_p;
	end;
end if;

select	ie_entrada_saida
into STRICT	ie_entrada_saida_w
from	operacao_estoque
where	cd_operacao_estoque = cd_operacao_estoque_w;

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material = cd_material_w;

begin
select	ie_integra
into STRICT	ie_integra_w
from (
	SELECT	ie_integra
	from	regra_integr_movto_estoque
	where	ie_integracao = coalesce(ie_integracao_p, 'EMS')
	and	ie_situacao = 'A'
	and (ie_entrada_saida = 'A' or ie_entrada_saida_w = ie_entrada_saida)
	and	coalesce(cd_operacao_estoque, cd_operacao_estoque_w) = cd_operacao_estoque_w
	and	coalesce(cd_material, cd_material_w) = cd_material_w
	and	coalesce(cd_grupo_material, cd_grupo_material_w) = cd_grupo_material_w
	and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w) = cd_subgrupo_material_w
	and	coalesce(cd_classe_material, cd_classe_material_w) = cd_classe_material_w
	and	coalesce(cd_local_estoque, cd_local_estoque_w) = cd_local_estoque_w
	order by cd_local_estoque,
	cd_material,
	cd_classe_material,
	cd_subgrupo_material,
	cd_grupo_material,
	cd_operacao_estoque) alias8 LIMIT 1;
exception
when others then
	ie_integra_w	:=	'N';
end;

ds_retorno_w := ie_integra_w;

return	ds_retorno_w;

end;
$body$
