CREATE OR REPLACE FUNCTION obter_se_interna_prescr (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10) := '';
nr_prescricao_w		bigint;
qt_proc_nao_interna_w	bigint;

C01 CURSOR FOR
	SELECT	nr_prescricao
	from	prescr_medica a
	where	a.nr_atendimento = nr_atendimento_p
	and (	SELECT 	count(*)
		from	prescr_procedimento b
		where	b.nr_prescricao = a.nr_prescricao) > 1;


BEGIN

open C01;
loop
fetch C01 into
	nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select 	count(*)
	into STRICT	qt_proc_nao_interna_w
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_w
	and	obter_se_gera_proc_inter_bpa(cd_procedimento, ie_origem_proced) = 'N';

	if (coalesce(qt_proc_nao_interna_w,0) > 0) then
		ds_retorno_w := ds_retorno_w||nr_prescricao_w||',';
	end if;

	end;
end loop;
close C01;

return	coalesce(substr(ds_retorno_w,1,length(ds_retorno_w)-1),'');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
