CREATE OR REPLACE FUNCTION obter_se_intervalo_estab ( cd_intervalo_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_mostrar_w	varchar(1) := 'S';
cont_w		bigint;


BEGIN

select	count(*)
into STRICT	cont_w
from	intervalo_estabelecimento
where	cd_intervalo	= cd_intervalo_p;

if (cont_w > 0) then
	select	coalesce(max(ie_mostrar),'S')
	into STRICT	ie_mostrar_w
	from	intervalo_estabelecimento
	where	cd_intervalo		= cd_intervalo_p
	and	cd_estab		= cd_estabelecimento_p;
end if;

return	ie_mostrar_w;

end;
$body$
LANGUAGE PLPGSQL
