CREATE OR REPLACE FUNCTION obter_se_intervalo_lib_mat ( cd_material_p bigint, cd_intervalo_p text) RETURNS varchar AS $body$
DECLARE


ie_exibir_w		varchar(1) := 'S';


BEGIN

if (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	select	coalesce(max('N'),'S')
	into STRICT	ie_exibir_w
	from	lib_material_intervalo
	where	cd_intervalo	= cd_intervalo_p
	and		coalesce(ie_tipo,'I') = 'M';

	if (ie_exibir_w = 'N') then
		select	coalesce(max('S'),'N')
		into STRICT	ie_exibir_w
		from	lib_material_intervalo
		where	cd_material	= cd_material_p
		and		cd_intervalo	= cd_intervalo_p
		and		coalesce(ie_tipo,'I') = 'M';
	end if;
end if;

return	ie_exibir_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
