CREATE OR REPLACE FUNCTION obter_se_intervalo_operacao (cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint, cd_intervalo_p text) RETURNS varchar AS $body$
DECLARE


cd_area_procedimento_w		bigint;
cd_especialidade_w		bigint;
cd_grupo_proc_w			bigint;
ie_utiliza_quantidade_ww	varchar(1) := 'N';

c01 CURSOR FOR
SELECT	ie_utiliza_quantidade
from	intervalo_procedimento
where	coalesce(nr_seq_proc_interno,coalesce(nr_seq_proc_interno_p,0)) = coalesce(nr_seq_proc_interno_p,0)
and	coalesce(cd_procedimento,cd_procedimento_p)		 = cd_procedimento_P
and	((coalesce(cd_procedimento::text, '') = '') or (ie_origem_proced = ie_origem_proced_p))
and	coalesce(cd_grupo_proc,cd_grupo_proc_w)		 = cd_grupo_proc_w
and	coalesce(cd_area_procedimento,cd_area_procedimento_w) = cd_area_procedimento_w
and	coalesce(cd_especialidade,cd_especialidade_w)	 = cd_especialidade_w
and	cd_intervalo = cd_intervalo_p
order by coalesce(cd_procedimento,0),
	coalesce(nr_seq_proc_interno,0),
	coalesce(cd_area_procedimento,0),
	coalesce(cd_especialidade,0),
	coalesce(cd_grupo_proc,0);


BEGIN

select	a.cd_grupo_proc,
	a.cd_area_procedimento,
	a.cd_especialidade
into STRICT	cd_grupo_proc_w,
	cd_area_procedimento_w,
	cd_especialidade_w
from	estrutura_procedimento_v a
where	a.cd_procedimento	= cd_procedimento_p
and	a.ie_origem_proced	= ie_origem_proced_p;

open C01;
loop
fetch C01 into
	ie_utiliza_quantidade_ww;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ie_utiliza_quantidade_ww	:= ie_utiliza_quantidade_ww;
end loop;
close C01;

return	ie_utiliza_quantidade_ww;

end;
$body$
