CREATE OR REPLACE FUNCTION obter_se_intervalo_prescricao ( cd_intervalo_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);
ie_se_necessario_w	varchar(1);


BEGIN

ie_retorno_w	:= 'N';

if (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then
	begin
	select   coalesce(ie_se_necessario,'N')
	into STRICT	 ie_se_necessario_w
	from	 intervalo_prescricao
	where	 cd_intervalo	= cd_intervalo_p;

	if (ie_opcao_p = 'S') then --Se necess√°rio
		ie_retorno_w := ie_se_necessario_w;
	end if;
	end;
end if;

return ie_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
