CREATE OR REPLACE FUNCTION obter_se_intervalo_proc_cpoe (nr_seq_proc_interno_p bigint, cd_intervalo_p text, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_area_procedimento_w	bigint;
cd_especialidade_w	bigint;
cd_grupo_proc_w		bigint;
ie_exibir_w		varchar(1) := 'S';
cd_procedimento_w	proc_interno.cd_procedimento%type;
ie_origem_proced_w	proc_interno.ie_origem_proced%type;
nr_seq_proc_int_w	intervalo_procedimento.nr_seq_proc_interno%type;

c01 CURSOR FOR
SELECT	ie_exibe_intervalo
from	intervalo_procedimento
where	coalesce(nr_seq_proc_interno,nr_seq_proc_int_w) = nr_seq_proc_int_w
and	coalesce(cd_procedimento,cd_procedimento_w)		 = cd_procedimento_w
and	((coalesce(cd_procedimento::text, '') = '') or (ie_origem_proced = ie_origem_proced_w))
and	coalesce(cd_grupo_proc,cd_grupo_proc_w)		 = cd_grupo_proc_w
and	coalesce(cd_area_procedimento,cd_area_procedimento_w) = cd_area_procedimento_w
and	coalesce(cd_especialidade,cd_especialidade_w)	 = cd_especialidade_w
and	coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_p, 0)) = coalesce(cd_setor_atendimento_p, 0)
and	cd_intervalo	= cd_intervalo_p
order by coalesce(cd_procedimento,0),
	coalesce(nr_seq_proc_interno,0),
	coalesce(cd_area_procedimento,0),
	coalesce(cd_especialidade,0),
	coalesce(cd_grupo_proc,0),
	coalesce(cd_setor_atendimento_p, 0);


BEGIN

select	max(cd_procedimento),
		max(ie_origem_proced)
into STRICT	cd_procedimento_w,
		ie_origem_proced_w
from	proc_interno
where	nr_sequencia	= nr_seq_proc_interno_p;

select	coalesce(max(a.cd_grupo_proc),0),
	coalesce(max(a.cd_area_procedimento),0),
	coalesce(max(a.cd_especialidade),0)
into STRICT	cd_grupo_proc_w,
	cd_area_procedimento_w,
	cd_especialidade_w
from	estrutura_procedimento_v a
where	a.cd_procedimento	= cd_procedimento_w
and	a.ie_origem_proced	= ie_origem_proced_w;

nr_seq_proc_int_w := nr_seq_proc_interno_p;

if (coalesce(nr_seq_proc_int_w::text, '') = '') then
	nr_seq_proc_int_w := 9999999;
end if;

open C01;
loop
fetch C01 into
	ie_exibir_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ie_exibir_w	:= ie_exibir_w;
end loop;
close C01;

return	ie_exibir_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
