CREATE OR REPLACE FUNCTION obter_se_intervalo_sugerido ( cd_material_p bigint, cd_intervalo_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'S';
ie_regra_existe_w	varchar(1);

BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_regra_existe_w
from	rep_intervalo_sugerido
where	cd_material = cd_material_p;

if (ie_regra_existe_w = 'S') and (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	rep_intervalo_sugerido
	where	cd_material = cd_material_p
	and		cd_intervalo = cd_intervalo_p;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
