CREATE OR REPLACE FUNCTION obter_se_intervalo ( cd_intervalo_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';
qt_intervalo_w		bigint := 1;
ie_somente_dieta_w	varchar(1);


BEGIN

if (ie_opcao_p = '1') then /* Prescrição, pasta Medicamentos */
	select	count(*)
	into STRICT	qt_intervalo_w
	from	intervalo_prescricao
	where	cd_intervalo	= cd_intervalo_p
	and	ie_prescricao_dieta in ('N','M','H','Q','O')
	and	obter_prescr_dieta_interv(cd_intervalo_p,'M') = 'S';

elsif (ie_opcao_p = '2') then /* Prescrição, pasta Materiais */
	select	count(*)
	into STRICT	qt_intervalo_w
	from	intervalo_prescricao
	where	cd_intervalo	= cd_intervalo_p
	and	ie_prescricao_dieta in ('N','A','H','Q','O')
	and	obter_prescr_dieta_interv(cd_intervalo_p,'A') = 'S';

elsif (ie_opcao_p = 'P') then /* Prescrição, pasta Procedimentos */
	select	count(*)
	into STRICT	qt_intervalo_w
	from	intervalo_prescricao
	where	cd_intervalo	= cd_intervalo_p
	and	ie_prescricao_dieta in ('N','P','H','Q')
	and	obter_prescr_dieta_interv(cd_intervalo_p,'P') = 'S';

elsif (ie_opcao_p = '5') then /* Prescrição, pasta Procedimentos, Medicamentos associados */
	select	count(*)
	into STRICT	qt_intervalo_w
	from	intervalo_prescricao
	where	cd_intervalo	= cd_intervalo_p
	and	ie_prescricao_dieta in ('N','P','M','H','Q');

elsif (ie_opcao_p = 'B') then /* Prescrição, pasta Banco de sangue */
	select	count(*)
	into STRICT	qt_intervalo_w
	from	intervalo_prescricao
	where	cd_intervalo	= cd_intervalo_p
	and	ie_prescricao_dieta in ('N','B','H','O')
	and	obter_prescr_dieta_interv(cd_intervalo_p,'B') = 'S';

elsif (ie_opcao_p = 'R') then /* Prescrição, pasta Recomendações */
	select	count(*)
	into STRICT	qt_intervalo_w
	from	intervalo_prescricao
	where	cd_intervalo	= cd_intervalo_p
	and	ie_prescricao_dieta in ('N','R','H','Q','O')
	and	obter_prescr_dieta_interv(cd_intervalo_p,'R') = 'S';

elsif (ie_opcao_p = '8') then /* Prescrição, pasta Nutrição, SNE */
	begin

	ie_somente_dieta_w := obter_param_usuario(924, 139, wheb_usuario_pck.Get_cd_perfil, wheb_usuario_pck.Get_nm_usuario, 0, ie_somente_dieta_w);

	if (ie_somente_dieta_w = 'S') then
		select	count(*)
		into STRICT	qt_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo	= cd_intervalo_p
		and	ie_prescricao_dieta in ('S','I','O')
		and	obter_prescr_dieta_interv(cd_intervalo_p,'N') = 'S';

	else
		select	count(*)
		into STRICT	qt_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo	= cd_intervalo_p
		and	ie_prescricao_dieta in ('S','I','H','Q','O')
		and	obter_prescr_dieta_interv(cd_intervalo_p,'N') = 'S';
	end if;
	end;

elsif (ie_opcao_p = '9') then /* Prescrição, pasta Nutrição, NPTs*/
	begin

	ie_somente_dieta_w := obter_param_usuario(924, 139, wheb_usuario_pck.Get_cd_perfil, wheb_usuario_pck.Get_nm_usuario, 0, ie_somente_dieta_w);

	if (ie_somente_dieta_w = 'S') then
		select	count(*)
		into STRICT	qt_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo	= cd_intervalo_p
		and	ie_prescricao_dieta in ('S','I','O')
		and	obter_prescr_dieta_interv(cd_intervalo_p,'C') = 'S';

	else
		select	count(*)
		into STRICT	qt_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo	= cd_intervalo_p
		and	ie_prescricao_dieta in ('S','I','H','Q','O')
		and	obter_prescr_dieta_interv(cd_intervalo_p,'C') = 'S';
	end if;
	end;

elsif (ie_opcao_p = '12') then /* Prescrição, pasta Nutrição, Suplemento */
	begin

	ie_somente_dieta_w := obter_param_usuario(924, 139, wheb_usuario_pck.Get_cd_perfil, wheb_usuario_pck.Get_nm_usuario, 0, ie_somente_dieta_w);

	if (ie_somente_dieta_w = 'S') then
		select	count(*)
		into STRICT	qt_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo	= cd_intervalo_p
		and	ie_prescricao_dieta in ('S','I','O')
		and	obter_prescr_dieta_interv(cd_intervalo_p,'T') = 'S';

	else
		select	count(*)
		into STRICT	qt_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo	= cd_intervalo_p
		and	ie_prescricao_dieta in ('S','I','H','Q','O')
		and	obter_prescr_dieta_interv(cd_intervalo_p,'T') = 'S';
	end if;
	end;

elsif (ie_opcao_p = '13') then /* Prescrição, pasta Nutrição, Dieta Oral */
	begin

	ie_somente_dieta_w := obter_param_usuario(924, 139, wheb_usuario_pck.Get_cd_perfil, wheb_usuario_pck.Get_nm_usuario, 0, ie_somente_dieta_w);

	if (ie_somente_dieta_w = 'S') then
		select	count(*)
		into STRICT	qt_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo	= cd_intervalo_p
		and	ie_prescricao_dieta in ('S','I','O')
		and	((obter_prescr_dieta_interv(cd_intervalo_p,'O') = 'S') or (obter_prescr_dieta_interv(cd_intervalo_p,'C') = 'S'));

	else
		select	count(*)
		into STRICT	qt_intervalo_w
		from	intervalo_prescricao
		where	cd_intervalo	= cd_intervalo_p
		and	ie_prescricao_dieta in ('S','I','H','Q','O')
		and	((obter_prescr_dieta_interv(cd_intervalo_p,'O') = 'S') or (obter_prescr_dieta_interv(cd_intervalo_p,'C') = 'S'));
	end if;
	end;

elsif (ie_opcao_p = '14') then /* Prescrição, pasta Hemodiálise */
	select	count(*)
	into STRICT	qt_intervalo_w
	from	intervalo_prescricao
	where	cd_intervalo	= cd_intervalo_p
	and	ie_prescricao_dieta in ('N','M','H','Q','O');

elsif (ie_opcao_p = '15') then /* Prescrição, pasta Soluções */
	select	count(*)
	into STRICT	qt_intervalo_w
	from	intervalo_prescricao
	where	cd_intervalo	= cd_intervalo_p
	and	ie_prescricao_dieta in ('N','L','H','Q','O')
	and	obter_prescr_dieta_interv(cd_intervalo_p,'S') = 'S';
elsif (ie_opcao_p = '16') then /*Lactário */
	select	count(*)
	into STRICT	qt_intervalo_w
	from	intervalo_prescricao
	where	cd_intervalo	= cd_intervalo_p
	and	ie_prescricao_dieta in ('C', 'H')
	and	obter_prescr_dieta_interv(cd_intervalo_p,'L') = 'S';

elsif (ie_opcao_p = 'G') then /*Gasoterapia e Itens Associados*/
	select	count(*)
	into STRICT	qt_intervalo_w
	from	intervalo_prescricao
	where	cd_intervalo	= cd_intervalo_p
	and		ie_gasoterapia = 'S'
	and	obter_prescr_dieta_interv(cd_intervalo_p,'G') = 'S';
end if;

if (qt_intervalo_w = 0) then
	ds_retorno_w	:= 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
