CREATE OR REPLACE FUNCTION obter_se_intervencao_lib ( nr_seq_interv_p bigint, cd_perfil_p bigint, cd_setor_atend_p bigint, nm_usuario_p text, ie_tipo_liberacao_p text default 'A') RETURNS varchar AS $body$
DECLARE


qt_retorno_w	bigint;
ie_tipo_liberacao_w	varchar(10);
cd_estabelecimento_w	bigint;
ds_retorno_w		varchar(10);

BEGIN

ie_tipo_liberacao_w	:= coalesce(ie_tipo_liberacao_p,'A');

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

if (ie_tipo_liberacao_w	= 'A') then
	select	count(*)
	into STRICT	qt_retorno_w
	from	pe_intervencao_lib
	where	nr_seq_intervencao					= nr_seq_interv_p
	and	ie_tipo_liberacao					= ie_tipo_liberacao_w
	and	coalesce(cd_perfil,cd_perfil_p)				= cd_perfil_p
	and	coalesce(cd_estabelecimento,cd_estabelecimento_w)		= cd_estabelecimento_w
	and	coalesce(cd_setor_atendimento,coalesce(cd_setor_atend_p,0))	= coalesce(cd_setor_atend_p,0)
	and	((coalesce(cd_setor_usuario::text, '') = '') or (obter_se_setor_usuario(cd_setor_usuario,nm_usuario_p) = 'S'));


	if (qt_retorno_w > 0) then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;

elsif (ie_tipo_liberacao_w	= 'I') then

	ds_retorno_w	:= 'S';
	select	count(*)
	into STRICT	qt_retorno_w
	from	pe_intervencao_lib
	where	nr_seq_intervencao				= nr_seq_interv_p
	and	ie_tipo_liberacao				= ie_tipo_liberacao_w;

	if (qt_retorno_w	> 0) then

		select	count(*)
		into STRICT	qt_retorno_w
		from	pe_intervencao_lib
		where	nr_seq_intervencao				= nr_seq_interv_p
		and	ie_tipo_liberacao				= ie_tipo_liberacao_w
		and		coalesce(cd_perfil,cd_perfil_p)			= cd_perfil_p
		and		coalesce(cd_estabelecimento,cd_estabelecimento_w)	= cd_estabelecimento_w
		and		coalesce(cd_setor_atendimento,coalesce(cd_setor_atend_p,0))	= coalesce(cd_setor_atend_p,0)
		and		((coalesce(cd_setor_usuario::text, '') = '') or (obter_se_setor_usuario(cd_setor_usuario,nm_usuario_p) = 'S'));

		if (qt_retorno_w > 0) then
			ds_retorno_w := 'S';
		else
			ds_retorno_w := 'N';
		end if;

	end if;

end if;

return	 ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
