CREATE OR REPLACE FUNCTION obter_se_interv_agora_acm_sn (cd_intervalo_p text) RETURNS varchar AS $body$
DECLARE


ie_acm_w		intervalo_prescricao.ie_acm%type;
ie_agora_w		intervalo_prescricao.ie_agora%type;
ie_se_necessario_w	intervalo_prescricao.ie_se_necessario%type;
ds_retorno_w		varchar(10) 	:= '';


BEGIN
if (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then


	select	coalesce(max(ie_agora),'N'),
		coalesce(max(ie_acm),'N'),
		coalesce(max(ie_se_necessario),'N')
	into STRICT	ie_agora_w,
		ie_acm_w,
		ie_se_necessario_w
	from	intervalo_prescricao
	where	cd_intervalo = cd_intervalo_p;

	if (ie_agora_w = 'S') then
		ds_retorno_w	:= 'AGORA';
	elsif (ie_acm_w = 'S') then
		ds_retorno_w	:= 'ACM';
	elsif (ie_se_necessario_w = 'S') then
		ds_retorno_w	:= 'SN';
	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
