CREATE OR REPLACE FUNCTION obter_se_interv_disp ( nr_seq_disp_p bigint, nr_seq_intervencao_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_retorno_w
from	dispositivo a,
	pe_proc_dispositivo b
where	b.nr_seq_dispositivo	=	a.nr_sequencia
and	a.nr_sequencia		=	nr_seq_disp_p
and	b.nr_seq_proc		=	nr_seq_intervencao_p
and	b.ie_acao in ('I','U');

return	ie_retorno_w;

end;
