CREATE OR REPLACE FUNCTION obter_se_interv_evento_unico ( cd_intervalo_p intervalo_prescricao.cd_intervalo%type) RETURNS varchar AS $body$
DECLARE


qt_operacao_w	intervalo_prescricao.qt_operacao%type;

BEGIN
select	coalesce(max(qt_operacao),0)
into STRICT	qt_operacao_w
from	intervalo_prescricao
where	cd_intervalo = cd_intervalo_p;

if (qt_operacao_w = 1) then
	return 'S';
end if;

return 'N';

end;
$body$
