CREATE OR REPLACE FUNCTION obter_se_interv_lib_med ( cd_material_p bigint, cd_intervalo_p text ) RETURNS varchar AS $body$
DECLARE


qt_regra_w		bigint;
qt_interv_medic_w	bigint;
ie_retorno_w		varchar(10)	:= 'S';

BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (cd_material_p > 0) then
	begin

	select	count(*)
	into STRICT	qt_regra_w
	from	lib_material_intervalo a
	where	cd_material = cd_material_p;

	if (qt_regra_w > 0) then

		select	count(*)
		into STRICT	qt_interv_medic_w
		from	lib_material_intervalo a
		where	cd_material = cd_material_p
		and	cd_intervalo = cd_intervalo_p;

		if (qt_interv_medic_w = 0) then

			ie_retorno_w := 'N';

		end if;

	end if;
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
