CREATE OR REPLACE FUNCTION obter_se_interv_regra ( cd_intervalo_p text, ie_regra_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(10)	:= 'S';
cd_perfil_w		bigint;


BEGIN

if (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') and (ie_regra_p IS NOT NULL AND ie_regra_p::text <> '') then
	select	coalesce(max('N'),'S')
	into STRICT	ie_retorno_w
	from	intervalo_prescr_regra
	where	cd_intervalo	= cd_intervalo_p;
	
	cd_perfil_w := obter_perfil_ativo;
	
	if (ie_retorno_w = 'N') then
		select	coalesce(max(ie_exibir),'S')
		into STRICT	ie_retorno_w
		from	intervalo_prescr_regra
		where	cd_intervalo	= cd_intervalo_p
		and		ie_regra		= ie_regra_p
		and 	coalesce(cd_perfil, cd_perfil_w) = cd_perfil_w;
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
