CREATE OR REPLACE FUNCTION obter_se_interv_superior_24h ( cd_intervalo_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_superior_w	varchar(1) := 'N';
ie_operacao_w	varchar(1);
qt_operacao_w	intervalo_prescricao.qt_operacao%type;
ie_24_h_w		varchar(1);


BEGIN

if (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then
	select	coalesce(max(qt_operacao), 1),
			coalesce(max(ie_24_h), 'N'),
			coalesce(max(ie_operacao), 'X')
	into STRICT	qt_operacao_w,
			ie_24_h_w,
			ie_operacao_w
	from	intervalo_prescricao
	where	cd_intervalo = cd_intervalo_p;

	if (ie_24_h_w = 'S') or (ie_operacao_w = 'D') or
		(ie_operacao_w = 'H' AND qt_operacao_w > 24) then
		ie_superior_w := 'S';
	end if;
end if;

return	ie_superior_w;

end;
$body$
