SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_int_prescr_sabin ( nr_prescricao_p bigint, ds_method_p text default null) RETURNS varchar AS $body$
DECLARE


qt_registro_w	bigint;


BEGIN

select	count(*) qt
into STRICT	qt_registro_w
from	prescr_procedimento p,
		prescr_medica a,
		exame_laboratorio e,
		lab_exame_equip l,
		equipamento_lab m
where	a.nr_prescricao   = nr_prescricao_p
and		a.nr_prescricao   = p.nr_prescricao
and		p.nr_seq_exame    = e.nr_seq_exame
and		e.nr_seq_exame    = l.nr_seq_exame
and		l.cd_equipamento  = m.cd_equipamento
and		upper(m.ds_sigla) = 'SABIN';

if (qt_registro_w = 0) then

   select	count(*) qt
   into STRICT	qt_registro_w
   from	proc_interno i,
         prescr_procedimento p ,
         regra_proc_interno_integra t
   where	i.ie_tipo in ('AP', 'APH', 'APC')
   and		p.nr_prescricao = nr_prescricao_p
   and 	i.nr_sequencia  = p.nr_seq_proc_interno
   and 	p.cd_cgc_laboratorio = '00718528000109'
   and	   	t.nr_seq_proc_interno = i.nr_sequencia
   and	   	t.ie_tipo_integracao = 4;
end if;

return qt_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
