CREATE OR REPLACE FUNCTION obter_se_isol_gera_alerta (nr_atendimento_p text) RETURNS varchar AS $body$
DECLARE


ie_gerar_alerta_w	varchar(1);
nr_seq_isol_w		bigint;

BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	coalesce(max(nr_Sequencia),0)
	into STRICT	nr_seq_isol_w
	from	motivo_isolamento_atend
	where	nr_atendimento = nr_atendimento_p;

	if (nr_seq_isol_w > 0) then

		select	coalesce(max(ie_gerar_alerta),'N')
		into STRICT	ie_gerar_alerta_w
		from	motivo_isolamento a,
			motivo_isolamento_atend b
		where	a.nr_sequencia = b.nr_seq_motivo_isol
		and	b.nr_sequencia		= nr_seq_isol_w;

	else
		select	coalesce(max(a.ie_gerar_alerta),'N')
		into STRICT	ie_gerar_alerta_w
		from	motivo_isolamento a,
				atendimento_precaucao b
		where	nr_atendimento = nr_atendimento_p
		and		a.nr_sequencia = b.nr_seq_motivo_isol
		and		exists ( SELECT	1
						from	cih_precaucao c
						where	b.nr_seq_precaucao = c.nr_sequencia
						and		c.IE_ISOLAMENTO = 'S');

	end if;

end if;

return	ie_gerar_alerta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
