CREATE OR REPLACE FUNCTION obter_se_item_aprazado ( nr_prescricao_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ie_aprazado_w	varchar(1);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_aprazado_w
	from	prescr_mat_hor
	where	nr_prescricao	= nr_prescricao_p
	and	nr_seq_material	= nr_seq_item_p
	and	ie_aprazado	= 'S'
	and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

end if;

return ie_aprazado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
