CREATE OR REPLACE FUNCTION obter_se_item_baixa_parcial ( nr_requisicao_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


qt_atendido_parcial_w			bigint 	:= 0;
ie_atendido_parcial_w			varchar(1)	:= 'N';
cd_estabelecimento_w				smallint;

/* Identifica se o item da requisição foi atendido com quantidade menor que a requisitada
	opção Baixar itens sem atendimento parcial - HSL */
BEGIN

if (nr_requisicao_p IS NOT NULL AND nr_requisicao_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	begin
		select	count(*)
		into STRICT	qt_atendido_parcial_w
		from	item_requisicao_material
		where	cd_material = cd_material_p
		and	nr_requisicao = nr_requisicao_p
		and	coalesce(obter_tipo_motivo_baixa_req(cd_motivo_baixa),0) > 0
		and	coalesce(qt_material_atendida,0) > 0
		and	ie_geracao = 'S';

		if (qt_atendido_parcial_w > 0) then
			ie_atendido_parcial_w		:= 'S';
		end if;
	end;
end if;

return	ie_atendido_parcial_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
