CREATE OR REPLACE FUNCTION obter_se_item_checkup_lib ( nr_seq_item_p bigint, cd_empresa_ref_p bigint, nr_seq_tipo_checkup_p bigint default null) RETURNS varchar AS $body$
DECLARE

ie_retorno_w		varchar(10)	:= 'S';
qt_reg_w			bigint;
nr_seq_tipo_checkup_w	bigint;

C01 CURSOR FOR
	SELECT	coalesce(IE_PERMITE,'S')
	into STRICT	qt_reg_w
	from	ETAPA_CHECKUP_ITEM_LIB
	where	nr_seq_item	= nr_seq_item_p
	and	coalesce(cd_empresa_ref,cd_empresa_ref_p)	= cd_empresa_ref_p
	and	coalesce(nr_seq_tipo_checkup,nr_seq_tipo_checkup_w)	= nr_seq_tipo_checkup_w
	order by coalesce(cd_empresa_ref,0),nr_seq_tipo_checkup desc;


BEGIN

nr_seq_tipo_checkup_w := coalesce(nr_seq_tipo_checkup_p,0);

select	count(*)
into STRICT	qt_reg_w
from	ETAPA_CHECKUP_ITEM_LIB
where	nr_seq_item	= nr_seq_item_p;

if (qt_reg_w	> 0 ) then
	ie_retorno_w	:= 'N';

	open C01;
	loop
	fetch C01 into
		ie_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
end if;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
