CREATE OR REPLACE FUNCTION obter_se_item_composto ( nr_prescricao_p text, nr_seq_material_p bigint, nr_agrupamento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_item_composto_w	varchar(1) := 'N';
qt_w			integer;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then

	select	count(*)
	into STRICT	qt_w
	from	prescr_material
	where	nr_prescricao = nr_prescricao_p
	and	nr_agrupamento = nr_agrupamento_p
	and	ie_agrupador = 1;

	if (qt_w > 1) then
		ie_item_composto_w	:= 'S';
	end if;

end if;

return	ie_item_composto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
