CREATE OR REPLACE FUNCTION obter_se_item_comunic_int_aval (nr_seq_regra_p bigint, nr_seq_avaliacao_p bigint) RETURNS varchar AS $body$
DECLARE



nr_seq_item_w		bigint;
ds_resultado_w		varchar(255);
ds_retorno_w		varchar(1) := 'S';
qt_reg_w		bigint;

C01 CURSOR FOR
	SELECT nr_seq_item,
	       ds_resultado
	FROM   REGRA_ITEM_COMUNIC_AVAL
	WHERE  nr_seq_regra = nr_seq_regra_p;


BEGIN
if (nr_seq_regra_p IS NOT NULL AND nr_seq_regra_p::text <> '') then

	select	count(*)
	into STRICT	qt_reg_w
	from	REGRA_ITEM_COMUNIC_AVAL
	where	nr_seq_regra	= nr_seq_regra_p;

	if (qt_reg_w	> 0) then

		ds_retorno_w	:= 'N';

		open	c01;
		loop
		fetch	c01 into
			  nr_seq_item_w,
			  ds_resultado_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select coalesce(max('S'),'N')
		into STRICT   ds_retorno_w
		from   REGRA_ITEM_COMUNIC_AVAL
		where  nr_seq_item = nr_seq_item_w
		and    ds_resultado_w = Aval(nr_seq_avaliacao_p,nr_seq_item_w);

		if (ds_retorno_w = 'N') then
			exit;
		end if;

		end;
		end loop;
		close c01;
	end if;
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
