CREATE OR REPLACE FUNCTION obter_se_item_confirmado ( nr_cot_compra_p bigint, nr_item_cot_compra_p bigint) RETURNS varchar AS $body$
DECLARE


ie_confirmado_integr_w		varchar(1) := 'N';


BEGIN

select	coalesce(ie_confirmado_integr,'N')
into STRICT	ie_confirmado_integr_w
from	cot_compra_item
