CREATE OR REPLACE FUNCTION obter_se_item_conf_gpt (nr_sequencia_p bigint, ie_tipo_item_p text, ie_tipo_usuario_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_procedimento_w	bigint;
nr_seq_dieta_w		bigint;
nr_seq_dialise_w	bigint;
nr_seq_gasoterapia_w	bigint;
nr_seq_hemoterapia_w	bigint;
nr_seq_intervencao_w	bigint;
nr_seq_material_w	bigint;
nr_seq_recomendacao_w	bigint;
ie_retorno_w		varchar(1) := 'N';


BEGIN

if (ie_tipo_item_p like 'EP') then --- Procedimento/Exames
	nr_seq_procedimento_w := nr_sequencia_p;

elsif (ie_tipo_item_p like 'MA') then --- Medicamento/Materiais/Soluções
	nr_seq_material_w := nr_sequencia_p;

elsif (ie_tipo_item_p like 'NUT') then --- Nutrição
	nr_seq_dieta_w := nr_sequencia_p;

elsif (ie_tipo_item_p like 'D') then ---Diálise
	nr_seq_dialise_w := nr_sequencia_p;

elsif (ie_tipo_item_p like 'R') then --- Recomendação
	nr_seq_recomendacao_w := nr_sequencia_p;

elsif (ie_tipo_item_p like 'H') then --- Hemoterapia
	nr_seq_hemoterapia_w := nr_sequencia_p;

elsif (ie_tipo_item_p like 'G') then --- Gasoterapia
	nr_seq_gasoterapia_w := nr_sequencia_p;

end if;

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_retorno_w
from	cpoe_item_gestao_analise
where (nr_seq_procedimento = nr_sequencia_p or
	nr_seq_dieta = nr_sequencia_p or
	nr_seq_dialise = nr_sequencia_p or
	nr_seq_gasoterapia = nr_sequencia_p or
	nr_seq_hemoterapia = nr_sequencia_p or
	nr_seq_intervencao = nr_sequencia_p or
	nr_seq_material = nr_sequencia_p or
	nr_seq_recomendacao = nr_sequencia_p)
	and	ie_tipo_usuario = ie_tipo_usuario_p
	and	ie_situacao = 'A';

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
