CREATE OR REPLACE FUNCTION obter_se_item_conf_pda (nr_seq_horario_p bigint, nr_prescricao_p bigint, nr_seq_item_p bigint, dt_horario_p timestamp, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1)	:= 'N';
qt_reg_w		integer;


BEGIN

if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then
	begin

	select	count(1)
	into STRICT	qt_reg_w
	from	administracao_lote_item
	where	nr_seq_horario = nr_seq_horario_p
	and		ie_situacao = 'S';

	if (qt_reg_w = 0) then
		begin

		if (ie_tipo_item_p = 'M') then

			select	count(1)
			into STRICT	qt_reg_w
			from	prescr_mat_alteracao
			where	nr_seq_horario = nr_seq_horario_p
			and		ie_alteracao = 58
			and		ie_evento_valido = 'S';

			if (qt_reg_w = 0) then

			    select	count(1)
				into STRICT	qt_reg_w
				from	prescr_solucao_evento
				where	nr_prescricao = nr_prescricao_p
				and		nr_seq_material = nr_seq_item_p
				and		ie_alteracao = 54
				and		ie_evento_valido = 'S'
				and		dt_horario = dt_horario_p;

			end if;

		elsif (ie_tipo_item_p in ('IA', 'C', 'G', 'CCG', 'CIG')) then

			select	count(1)
			into STRICT	qt_reg_w
			from	prescr_mat_alteracao
			where	dt_horario = dt_horario_p
			and		nr_prescricao = nr_prescricao_p
			and		nr_seq_procedimento = nr_seq_item_p
			and		ie_alteracao = 58
			and		ie_evento_valido = 'S';

		elsif (ie_tipo_item_p in ('GAS')) then

			select	count(1)
			into STRICT	qt_reg_w
			from	prescr_gasoterapia_evento
			where	nr_seq_horario = nr_seq_horario_p
			and		ie_evento = 'CR'
			and		ie_evento_valido = 'S'
			and		nr_seq_gasoterapia = nr_seq_item_p;

		elsif (ie_tipo_item_p in ('SOL')) then

			select	count(1)
			into STRICT	qt_reg_w
			from	prescr_solucao_evento
			where	nr_prescricao = nr_prescricao_p
			and		nr_seq_solucao = nr_seq_item_p
			and		ie_alteracao = 54
			and		dt_horario = dt_horario_p
			and		ie_evento_valido = 'S';

		end if;

		end;
	end if;

	end;
end if;

if (qt_reg_w > 0) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
