CREATE OR REPLACE FUNCTION obter_se_item_dif_adep (nr_prescricao_p bigint, nr_seq_item_p bigint, nr_agrupamento_p bigint, ie_tipo_item_p text, ie_verificar_observacao_p text, observacao_p text ) RETURNS varchar AS $body$
DECLARE


ie_diferenciado_w	varchar(1);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') and (nr_agrupamento_p IS NOT NULL AND nr_agrupamento_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_diferenciado_w
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_p
	and	nr_sequencia_diluicao	= nr_seq_item_p
	and	ie_agrupador		in (3,7,9);

	if (ie_diferenciado_w = 'N') then

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_diferenciado_w
		from	prescr_material
		where	nr_prescricao	= nr_prescricao_p
		and	nr_sequencia	> nr_seq_item_p
		and	nr_agrupamento	= nr_agrupamento_p;

		if (coalesce(ie_verificar_observacao_p,'N') = 'S') and (ie_diferenciado_w = 'N') and ((trim(both observacao_p) IS NOT NULL AND (trim(both observacao_p))::text <> '')) then
		   ie_diferenciado_w := 'S';

		end if;

		if (ie_diferenciado_w = 'N') then --OS147839
			select	CASE WHEN coalesce(obter_diluicao_medic(nr_seq_item_p,nr_prescricao_p)::text, '') = '' THEN  'N'  ELSE 'S' END
			into STRICT	ie_diferenciado_w
			;


		end if;

	end if;

end if;

return ie_diferenciado_w;

end;
$body$
LANGUAGE PLPGSQL
