CREATE OR REPLACE FUNCTION obter_se_item_disp (cd_estabelecimento_p usuario.cd_estabelecimento%type, cd_material_p material.cd_material%type) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);
ie_disp_mercado_w	material.ie_disponivel_mercado%type;


BEGIN

	ie_retorno_w := 'S';

	if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

		ie_disp_mercado_w := coalesce(obter_se_material_disponivel(cd_material_p), 'S');

		if (ie_disp_mercado_w = 'N') then

			ie_retorno_w := 'N';

		elsif (ie_disp_mercado_w = 'E') then

			if (coalesce(obter_se_material_padronizado(cd_estabelecimento_p, cd_material_p), 'N') = 'S') then

				if (coalesce(obter_saldo_disp_estoque(cd_estabelecimento_p, cd_material_p, null, clock_timestamp()), 0) = 0) then

					ie_retorno_w := 'N';

				end if;

			end if;

		end if;

	end if;

	return ie_retorno_w;

end;
