CREATE OR REPLACE FUNCTION obter_se_item_incons_farm ( nr_prescricao_p bigint, nr_sequencia_p bigint, nr_seq_solucao_P bigint DEFAULT 0) RETURNS varchar AS $body$
DECLARE


ie_inconsistente_w	varchar(1);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_inconsistente_w
from	prescr_material_incon_farm
where	nr_prescricao	= nr_prescricao_p
and (nr_seq_material = nr_sequencia_p or nr_seq_solucao = nr_seq_solucao_p)
and		coalesce(ie_situacao,'A') = 'A';

return	ie_inconsistente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
