CREATE OR REPLACE FUNCTION obter_se_item_inv_bloqueio ( nr_seq_inventario_p bigint, nr_seq_item_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w		bigint;
nm_usuario_w		varchar(15);
dt_atualizacao_w		timestamp;
dt_tentativa_w		timestamp;
ie_bloqueado_w		varchar(1);
qt_min_controle_inv_w	bigint;
qt_min_diferenca_w		double precision;
ds_retorno_w		varchar(255);


BEGIN
ie_bloqueado_w	:= 'N';

/*Pega o tempo para controle do item do invent치rio conforme par칙metro [199] do PalmWeb ( Usu치rio que est치 realizando a contagem )*/

qt_min_controle_inv_w := obter_param_usuario(88, 199, null, nm_usuario_p, cd_estabelecimento_p, qt_min_controle_inv_w);

if (qt_min_controle_inv_w > 0) then
	begin

	select	coalesce(max(nr_sequencia), 0)
	into STRICT	nr_sequencia_w
	from	inventario_bloqueio
	where	nr_seq_inventario 	= nr_seq_inventario_p
	and	nr_seq_item 	= nr_seq_item_p;

	if (nr_sequencia_w > 0) then
		begin


		select	nm_usuario,
			dt_atualizacao
		into STRICT	nm_usuario_w,
			dt_atualizacao_w
		from	inventario_bloqueio
		where	nr_sequencia	= nr_sequencia_w;

		if (nm_usuario_w 	= nm_usuario_p) then
			ie_bloqueado_w	:= 'N';

		elsif (nm_usuario_w <> nm_usuario_p) then
			begin

			dt_tentativa_w	:= clock_timestamp();
			qt_min_diferenca_w	:= ((dt_tentativa_w - dt_atualizacao_w) * 1440);

			ie_bloqueado_w	:= 'S';
			if (qt_min_diferenca_w < qt_min_controle_inv_w) then
				ie_bloqueado_w	:= 'S';
			else
				begin
				ie_bloqueado_w	:= 'N';
				end;
			end if;

			end;
		end if;
		end;
	end if;
	end;
end if;

return ie_bloqueado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
