CREATE OR REPLACE FUNCTION obter_se_item_lib_perfil ( cd_perfil_p bigint, ie_exibir_pc text, ie_exibir_sc text, ie_exibir_tc text default 'S') RETURNS varchar AS $body$
DECLARE

			
ds_retorno_w		varchar(1) := 'N';			
ie_nivel_atencao_w	varchar(1);


BEGIN

select	coalesce(ie_nivel_atencao,'T')
into STRICT	ie_nivel_atencao_w
from	perfil
where	cd_perfil = cd_perfil_p;

if (ie_nivel_atencao_w = 'P') and (coalesce(ie_exibir_pc,'N') = 'S') then
	ds_retorno_w := 'S';
elsif (ie_nivel_atencao_w = 'S') and (coalesce(ie_exibir_sc,'N') = 'S') then
	ds_retorno_w := 'S';
elsif (ie_nivel_atencao_w = 'T') and (coalesce(ie_exibir_tc,'S') = 'S') then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
