SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_item_locale (nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_return_w varchar(1) := 'S';
qt_reg_w bigint;

BEGIN



select coalesce(max('S'), 'N')
into STRICT ds_return_w
from prontuario_item_locale
where nr_seq_item = nr_seq_item_p
and ds_locale = pkg_i18n.get_user_locale;

if (ds_return_w = 'N')then
	select count(1)
	into STRICT qt_reg_w
	from prontuario_item_locale
	where nr_seq_item = nr_seq_item_p;

	if (qt_reg_w = 0)then
		ds_return_w := 'S';
	end if;

end if;

return	ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
