CREATE OR REPLACE FUNCTION obter_se_item_mipres (nr_prescricao_p prescr_mipres.nr_prescricao%type, nr_atendimento_p prescr_mipres.nr_atendimento%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w             varchar(1);
ie_eps_w                 convenio_plano.ie_eps%type;
ie_verifica_existencia_w bigint;


BEGIN
  begin
    select coalesce(max(a.ie_eps),'N')
    into STRICT   ie_eps_w
    from   convenio_plano a
    where  a.cd_plano = obter_plano_convenio_atend(nr_atendimento_p,'C')
    and    a.cd_convenio = obter_convenio_atendimento(nr_atendimento_p);
  exception
    when no_data_found then ie_eps_w := null;
  end;

  if (ie_eps_w = 'S') then
    select case
      when exists(select 1
                  from   cpoe_itens_mipres_v a
                  where  ((nr_prescricao_p > 0 and a.nr_prescricao = nr_prescricao_p and coalesce(a.nr_seq_receita_amb::text, '') = '')
                  or (nr_prescricao_p = 0 and a.nr_atendimento = nr_atendimento_p and (a.nr_seq_receita_amb IS NOT NULL AND a.nr_seq_receita_amb::text <> '')))
                  and    a.ie_nopbs = 'S'  LIMIT 1) then 1 
      else 0
        end into STRICT ie_verifica_existencia_w;
  end if;

  if (ie_verifica_existencia_w = 1) then
    ds_retorno_w := 'S';
  else
    ds_retorno_w := 'N';
  end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
