SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_item_nf_devolvido ( nr_sequencia_p bigint, nr_item_nf_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w			bigint;
ie_devolvido_w			varchar(1) := 'N';


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	nota_fiscal a,
	nota_fiscal_item b
where	a.nr_sequencia = b.nr_sequencia
and	a.nr_sequencia = nr_sequencia_p
and	b.nr_item_nf = nr_item_nf_p
and exists (
	SELECT	1
	from	nota_fiscal x,
		nota_fiscal_item y
	where	x.nr_sequencia = y.nr_sequencia
	and	x.nr_sequencia_ref = a.nr_sequencia
	and	y.nr_item_nf = b.nr_item_nf
	
union

	SELECT	1
	from	nota_fiscal_item y
	where	y.nr_seq_nf_orig = a.nr_sequencia
	and	y.nr_seq_item_nf_orig = b.nr_item_nf);

if (qt_existe_w > 0) then
	ie_devolvido_w := 'S';
end if;

return	ie_devolvido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
