CREATE OR REPLACE FUNCTION obter_se_item_oci_pend_autor (nr_ordem_compra_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE



ie_autorizado_w		varchar(15) := 'S';
nr_item_oci_w		integer;
cd_material_w		bigint;
ie_existe_autor_w	varchar(5);


C01 CURSOR FOR
	SELECT	nr_item_oci,
		cd_material
	from	ordem_compra_item
	where	nr_ordem_compra = nr_ordem_compra_p
	and	ie_autorizado_w = 'S'
	order by nr_item_oci;




BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then

	open C01;
	loop
	fetch C01 into
		nr_item_oci_w,
		cd_material_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		begin
		select	'N'
		into STRICT	ie_autorizado_w
		from	autorizacao_convenio a,
			estagio_autorizacao b,
			material_autorizado c
		where	a.nr_seq_estagio = b.nr_sequencia
		and	a.nr_sequencia   = c.nr_sequencia_autor
		and	a.nr_atendimento = nr_atendimento_p
		and	c.cd_material	 = cd_material_w
		and	b.ie_interno	 <> '10'
		and	a.ie_tipo_autorizacao = '4'  LIMIT 1;
		exception
		when others then
			ie_autorizado_w := 'S';
		end;

		end;
	end loop;
	close C01;

end if;

return	ie_autorizado_w;

end;
$body$
