CREATE OR REPLACE FUNCTION obter_se_item_oc_cancelado ( nr_ordem_compra_p bigint, nr_item_oci_p bigint) RETURNS varchar AS $body$
DECLARE


ie_cancelado_w		varchar(1) := 'N';
qt_existe_w		bigint;

BEGIN

select	count(*)
into STRICT	qt_existe_w
from	ordem_compra_item_entrega
where	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p;

if (qt_existe_w > 0) then

	select	count(*)
	into STRICT	qt_existe_w
	from	ordem_compra_item_entrega
	where	nr_ordem_compra = nr_ordem_compra_p
	and	nr_item_oci = nr_item_oci_p
	and	coalesce(dt_cancelamento::text, '') = '';

	if (qt_existe_w > 0) then
		ie_cancelado_w := 'N';
	else
		ie_cancelado_w := 'S';
	end if;
end if;


return	ie_cancelado_w;

end;
$body$
