CREATE OR REPLACE FUNCTION obter_se_item_ordem_medica ( nr_prescricao_p bigint, nr_seq_item_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_opcao_p
 S - Solucao
 M - Medicamento
 SNE - Nutritional supplement
 TPN - Nutrition TPN
 DI - Dialysis
 OX - Gas Therapy
 HM - Bloob / Blood Products
*/
ie_ordem_medica_w	char(1)	:= 'N';
ds_sql_w			varchar(10000);

	cCursor REFCURSOR;
ds_bind_cCursor_w	sql_pck.t_dado_bind;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') and (ie_opcao_p in ('S','M','SNE','TPN','DI','OX','HM')) then
	ds_sql_w	:=
		' select	coalesce(max(''S''),''N'') ' ||
		' from		prescr_medica_ordem ' ||
		' where		nr_prescricao =  :nr_prescricao_p ' ||
		' and		rownum = 1 ';

	if (ie_opcao_p = 'S') then
		ds_sql_w	:= ds_sql_w || ' and		nr_seq_solucao	= :nr_seq_item_p ';
	elsif (ie_opcao_p = 'M') then
		ds_sql_w	:= ds_sql_w || ' and		nr_seq_material	= :nr_seq_item_p ';
	elsif (ie_opcao_p = 'SNE') THEN
		ds_sql_w	:= ds_sql_w || ' and		nr_seq_dieta_enteral = :nr_seq_item_p ';
  elsif (ie_opcao_p = 'TPN') THEN
		ds_sql_w	:= ds_sql_w || ' and		nr_seq_nut_pac	= :nr_seq_item_p ';
  elsif (ie_opcao_p = 'DI') THEN
		ds_sql_w	:= ds_sql_w || ' and		nr_seq_solucao	= :nr_seq_item_p ';
	elsif (ie_opcao_p = 'OX') THEN
		ds_sql_w	:= ds_sql_w || ' and		nr_seq_gasoterapia	= :nr_seq_item_p ';
	elsif (ie_opcao_p = 'HM') THEN
		ds_sql_w	:= ds_sql_w || ' and		nr_seq_hemoterapia	= :nr_seq_item_p ';
	end if;

    ds_bind_cCursor_w := sql_pck.bind_variable(':nr_prescricao_p', nr_prescricao_p, ds_bind_cCursor_w);
    ds_bind_cCursor_w := sql_pck.bind_variable(':nr_seq_item_p', nr_seq_item_p, ds_bind_cCursor_w);

    ds_bind_cCursor_w := sql_pck.executa_sql_cursor(ds_sql_w, ds_bind_cCursor_w);

    loop
	fetch cCursor into	
		ie_ordem_medica_w;
	EXIT WHEN NOT FOUND; /* apply on cCursor */
    end loop;

end if;

return	ie_ordem_medica_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
