CREATE OR REPLACE FUNCTION obter_se_item_ordem_nf ( nr_ordem_compra_p bigint, nr_item_oci_p bigint) RETURNS varchar AS $body$
DECLARE


qt_reg_w			integer;
ds_retorno_w		varchar(1);


BEGIN

ds_retorno_w := 'N';

select	count(*)
into STRICT	qt_reg_w
from	nota_fiscal a,
	nota_fiscal_item b
where	a.nr_sequencia = b.nr_sequencia
and	a.ie_situacao = '1'
and	b.nr_ordem_compra = nr_ordem_compra_p
and	b.nr_item_oci = nr_item_oci_p;

if (qt_reg_w > 0) then
	ds_retorno_w := 'S';
end	if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
