CREATE OR REPLACE FUNCTION obter_se_item_proj_recurso ( nr_seq_proj_rec_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_existe_w		varchar(1) := 'N';
qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	projeto_recurso_item
where	nr_seq_proj_rec = nr_seq_proj_rec_p
and	cd_material = cd_material_p;

if (qt_existe_w > 0) then
	ie_existe_w := 'S';
end if;

return	ie_existe_w;

end;
$body$
