CREATE OR REPLACE FUNCTION obter_se_item_recomendacao ( cd_item_p bigint, cd_setor_atendimento_p bigint, ie_tipo_item_p text, ie_via_aplicacao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1);
qt_registo_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_registo_w
from    rep_recomendacao_solucao
where	((cd_material          = cd_item_p AND ie_tipo_item_p	= 'M') or
	 (cd_dieta		= cd_item_p AND 'D'			= ie_tipo_item_p))
and	cd_setor_atendimento = cd_setor_atendimento_p
and	coalesce(ie_via_aplicacao,ie_via_aplicacao_p)	= ie_via_aplicacao_p
and	coalesce(ie_forma_lancamento,'L') = 'P'  LIMIT 1;

if (qt_registo_w > 0) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return coalesce(ds_retorno_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
