CREATE OR REPLACE FUNCTION obter_se_item_regra_ativo ( nr_seq_regra_p bigint, nr_seq_lanc_p bigint) RETURNS varchar AS $body$
DECLARE


cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
cd_material_w		integer;
ie_situacao_w		varchar(1);
ds_retorno_w		varchar(1)	:= 'S';


BEGIN

select	cd_procedimento,
	ie_origem_proced,
	cd_material
into STRICT	cd_procedimento_w,
	ie_origem_proced_w,
	cd_material_w
from	regra_lanc_aut_pac
where	nr_seq_regra	= nr_seq_regra_p
and	nr_seq_lanc	= nr_seq_lanc_p
and 	coalesce(ie_adic_orcamento,'N') = 'N';

if (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '') and (ie_origem_proced_w IS NOT NULL AND ie_origem_proced_w::text <> '') then
	select	coalesce(ie_situacao, 'A')
	into STRICT	ie_situacao_w
	from	procedimento
	where	cd_procedimento		= cd_procedimento_w
	and	ie_origem_proced	= ie_origem_proced_w;

	if (ie_situacao_w	= 'I') then
		ds_retorno_w	:= 'N';
	end if;

end if;

if (cd_material_w IS NOT NULL AND cd_material_w::text <> '') then
	select	coalesce(ie_situacao, 'A')
	into STRICT	ie_situacao_w
	from	material
	where	cd_material	= cd_material_w;

	if (ie_situacao_w	= 'I') then
		ds_retorno_w	:= 'N';
	end if;

end if;

Return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
