CREATE OR REPLACE FUNCTION obter_se_item_req_barras (nr_requisicao_p bigint, nr_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
nr_seq_cor_w	bigint;


BEGIN

select	coalesce(a.nr_seq_cor_exec,0)
into STRICT	nr_seq_cor_w
from	item_requisicao_material a
where	a.nr_requisicao = nr_requisicao_p
and	a.nr_sequencia = nr_item_p;

if (nr_seq_cor_w = 103) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
