CREATE OR REPLACE FUNCTION obter_se_item_sc_parc_entregue ( nr_solic_compra_p bigint, nr_item_solic_compra_p bigint) RETURNS varchar AS $body$
DECLARE


ie_pendente_w 				varchar(1) := 'N';
qt_entrega_solicitada_w		solic_compra_item_entrega.qt_entrega_solicitada%type;
qt_real_entrega_w		    ordem_compra_item_entrega.qt_real_entrega%type;



BEGIN
	select coalesce(sum(b.qt_entrega_solicitada),0)
    into STRICT qt_entrega_solicitada_w
	from 	solic_compra_item a,
		solic_compra_item_entrega b
	where 	a.nr_solic_compra = b.nr_solic_compra
	and	a.nr_item_solic_compra = b.nr_item_solic_compra
	and	a.nr_solic_compra = nr_solic_compra_p
    and a.nr_item_solic_compra = nr_item_solic_compra_p
	and	exists (	SELECT 	1
				from 	ordem_compra_item d 
				where 	d.nr_solic_compra = b.nr_solic_compra 
				and 	d.nr_item_solic_compra = b.nr_item_solic_compra);

	select 	coalesce(sum(b.qt_real_entrega),0)
	into STRICT	qt_real_entrega_w
	from	ordem_compra_item_entrega b,
		ordem_compra_item a
	where	b.nr_ordem_compra = a.nr_ordem_compra
	and	b.nr_item_oci = a.nr_item_oci
	and	a.nr_solic_compra = nr_solic_compra_p
	and	a.nr_item_solic_compra = nr_item_solic_compra_p;
	
	if (qt_real_entrega_w > 0 and qt_real_entrega_w < qt_entrega_solicitada_w) then
		ie_pendente_w := 'S';
	end if;	

return	ie_pendente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
