CREATE OR REPLACE FUNCTION obter_se_item_urgente (nr_prescricao_p bigint, nr_sequencia_p bigint, cd_material_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


ie_urgente_w	varchar(1)	:= 'N';


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
		select	coalesce(m.ie_urgencia,'N') ie_item_urgente
		into STRICT	ie_urgente_w
		from	prescr_material m
		where	m.nr_prescricao	= nr_prescricao_p
		and	m.nr_sequencia	= nr_sequencia_p
		and	m.cd_material	= cd_material_p;

	elsif (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') and (ie_origem_proced_p IS NOT NULL AND ie_origem_proced_p::text <> '') then
		select	coalesce(p.ie_urgencia,'N') ie_item_urgente
		into STRICT	ie_urgente_w
		from	prescr_procedimento p
		where	p.nr_prescricao		= nr_prescricao_p
		and	p.nr_sequencia		= nr_sequencia_p
		and	p.cd_procedimento	= cd_procedimento_p
		and	ie_origem_proced	= ie_origem_proced_p;
	end if;
end if;

RETURN	ie_urgente_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
