CREATE OR REPLACE FUNCTION obter_se_item_valido_prescr ( nr_atendimento_p bigint, nr_prescricao_p bigint, dt_periodo_inicial_p timestamp, dt_periodo_final_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_item_valido_w	varchar(1):= 'N';
qt_periodo_w		bigint;


BEGIN

ie_item_valido_w:= 'N';

if (coalesce(nr_prescricao_p,0) > 0) then

	select	count(*)
	into STRICT	qt_periodo_w
	from 	prescr_medica
	where 	nr_atendimento = nr_atendimento_p
	and 	nr_prescricao = nr_prescricao_p
	and 	coalesce(dt_inicio_prescr, dt_periodo_inicial_p) = dt_periodo_inicial_p
	and 	coalesce(dt_validade_prescr, dt_periodo_final_p) =  dt_periodo_final_p;

	if (qt_periodo_w > 0) then
		ie_item_valido_w:= 'S';
	end if;

end if;

return	ie_item_valido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
