CREATE OR REPLACE FUNCTION obter_se_itens_glosados (nr_seq_ret_item_p bigint) RETURNS varchar AS $body$
DECLARE


qt_itens_glosados_w	bigint;
ds_retorno_w		varchar(5);


BEGIN

ds_retorno_w := 'N';

select	count(*)
into STRICT	qt_itens_glosados_w
from	convenio_retorno_glosa a
where	a.nr_seq_ret_item	= nr_seq_ret_item_p;

if (qt_itens_glosados_w > 0) then
	ds_retorno_w := 'S';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
