CREATE OR REPLACE FUNCTION obter_se_jejum_prescrito (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_jejum_w	varchar(1) := 'N';
cont_w		bigint;


BEGIN

select	count(*)
into STRICT	cont_w
from	prescr_medica b,
	rep_jejum a
where	b.nr_atendimento	= nr_atendimento_p
and	b.nr_prescricao		= a.nr_prescricao
and	trunc(clock_timestamp()) 		= trunc(coalesce(a.dt_inicio, b.dt_inicio_prescr));

if (cont_w > 0) then
	ie_jejum_w	:= 'S';
else
	select	count(*)
	into STRICT	cont_w
	from	prescr_medica b,
		rep_jejum a
	where	b.nr_atendimento	= nr_atendimento_p
	and	b.nr_prescricao		= a.nr_prescricao
	and	clock_timestamp() between coalesce(a.dt_inicio, b.dt_inicio_prescr) and coalesce(a.dt_fim, b.dt_validade_prescr)
	and	(coalesce(b.dt_liberacao, b.dt_liberacao_medico) IS NOT NULL AND (coalesce(b.dt_liberacao, b.dt_liberacao_medico))::text <> '')
	and	coalesce(a.ie_suspenso,'N') = 'N';

	if (cont_w > 0) then
		ie_jejum_w	:= 'S';
	end if;
end if;

return	ie_jejum_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
